import React from "react"

function CreateArea(props) {


    return (
      <div>
        <form>
          <input
            name="title"
            onChange={handleChange}
            value={note.title}
            placeholder="Title"
          />
          <textarea
            name="content"
            onChange={handleChange}
            value={note.content}
            placeholder="Take a note..."
            rows="3"
          />
          <button onClick={submitNote}>Add</button>
        </form>
      </div>
    );
}

export default CreateArea
